/**
 * Additional functional component for the map view on the shop lookup page on the application.
 * Displays a circle based on the user-inputted radius with all shops displayed in that radius.
 * Uses the OpenStreetMap database.
 */

import { CSSProperties, FC, useEffect, useRef } from "react";
import { MapContainer, TileLayer, useMap, Marker, Popup, Circle, Tooltip } from "react-leaflet";
import { Icon, LatLngExpression } from "leaflet";
import { Shop } from "../types";

interface FitRadiusProps {
    center: LatLngExpression;
    radius: number;
}

interface ListMapProps {
    center: LatLngExpression;
    radius: number;
    style: CSSProperties;
    shops: Shop[];
}

const markerIcon = new Icon({
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    iconUrl:
        "data:image/png;base64,",
});

const FitRadius: FC<FitRadiusProps> = ({ center, radius }) => {
    const map = useMap();
    const circleRef = useRef();
    useEffect(() => {
        map.fitBounds(circleRef.current.getBounds());
    }, [radius]);

    return <Circle ref={circleRef} center={center} radius={radius * 1000} pathOptions={{ color: "blue", opacity: 0.3, fill: false }} />;
};

const ListMap: FC<ListMapProps> = ({ center, radius, shops, style }) => {
    return (
        <>
            {center ? (
                <MapContainer center={center} zoom={20} style={style}>
                    <FitRadius center={center} radius={radius} />
                    <TileLayer
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    {shops.map((shop, i) => (
                        <Marker key={i} position={[shop.latitude, shop.longitude]} icon={markerIcon}>
                            <Popup>
                                <h5>{shop.name}</h5>
                                <div className="text-muted">
                                    <div>{shop.address}</div>
                                    <div className="mt-2">
                                        <i className="fa-solid fa-phone me-2" />
                                        {shop.phone}
                                    </div>
                                </div>
                            </Popup>
                            {/* <Tooltip permanent opacity={0.5}>
                                {shop.label}
                            </Tooltip> */}
                        </Marker>
                    ))}
                </MapContainer>
            ) : (
                ""
            )}
        </>
    );
};

export default ListMap;
